import React ,{ useEffect, useState, useRef} from 'react';
import styled from 'styled-components';
import { atom, useRecoilState } from 'recoil';
import Array from './Array';


const Wrapper = styled.section`
    width: 100%;
    max-width: 1680px;

    .button {
        margin: 50px 70px;
        display: none;
        background-color: linear-gradient(to right, #4364f7, #23d5ab);
    }
`;


const List = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    row-gap: 10px;

    .productContent {
        display: flex;
        justify-content: center;
    }
    .product {
        width:400px;
        height: 400px;
        margin: 10px;
    }
`;

const OutsideBox = styled.div`
    border: 2px solid #dfdfdf;
    border-radius: 10px;
    margin: 40px 70px 10px;
    background-color: linear-gradient(to right, #4364f7, #23d5ab);
`;

const InsideBox = styled.div`
    border: 1px solid #dfdfdf;
    padding: 5px 0;  

    .arrNum {
        font-size: 20px;
        color: #4c4c4c;
        margin: 10px 5px;
        border: 2px solid #c5c5c5; 
        border-radius: 5px;
        padding: 0 10px;
        background-color: linear-gradient(to right, #4364f7, #23d5ab);

        &:hover {
            cursor: pointer;
        }
    }
`;

const Button = styled.div`
        margin: 30px 70px;
        font-size: 18px;

    .input-file-button {
        padding: 6px 25px;
        background-color: #0358ff;
        border-radius: 4px;
        color: white;

        &:hover {
            cursor: pointer;
        }
    }
`;

const state = atom({
    key: 'grid',
    default: [],
});

const Grid = (id, src) => {

    const [imageList, setImageList] = useRecoilState(state);  

    const onFileChange = (e) => {
    const { target : {files} } = e;
        const theFile = files[0];
        const reader = new FileReader(); //객체를 이용해 컴퓨터에 저장하는 것을 가능하게
        reader.readAsDataURL(theFile); //읽을 객체
        reader.onloadend = (finishedEvent) =>{ //읽기 동작이 성공적으로 완료 되었을 때 발생
            const { currentTarget: { result } } = finishedEvent;
            setImageList((imageList) => [...imageList, {src: result , id: imageList.length, theFile}]);
        }
    }

    const removeItems = (id) => {
        setImageList(imageList.filter(imageList => imageList.id !== id));
    }

    console.log(imageList);
    return(
        <Wrapper>
            <OutsideBox>
                <InsideBox id={id} src={src}>
                    {imageList.map((image) => {
                        return (
                            <span className="arrNum" id={imageList.id} onClick={() => removeItems(imageList.id)}>{`${image.id} - ${image.theFile.name}`}</span>
                        );
                    })}
                </InsideBox>
            </OutsideBox>
                <Button>
                    <label className='input-file-button' for='input-file'>업로드</label>
                    <input className='button' id='input-file' type='file' onChange={onFileChange} multiple/> 
                </Button>
                <List> 
                {imageList.map((image) => {
                    return (
                        <div className="productContent">
                            <img className='product' src={image.src} />
                        </div>
                    );
                })}
                </List>
            </Wrapper>
    );
};

export default Grid;
