import React, { useState } from 'react';
import styled from 'styled-components';
import { atom, useRecoilState } from 'recoil';


const Wrapper = styled.section`
    width: 100%;
    .button {
        margin-top: 50px;
    }
`;

const Img = styled.div`
    // display: grid;
    // grid-template-rows: repeat(3, 1fr); 
    display: grid;
grid-template-rows: repeat(4, 1fr),
grid-template-columns

    .product {
        width:500px;
        height: 500px;
        margin: 10px;
    }
`;

const state = atom({
    key: 'grid',
    default: [],
});

const Grid = () => {

    const [imageList, setImageList] = useRecoilState(state);

    const onFileChange = (e) => {
    const { target : {files} } = e;
        const theFile = files[0];
        const reader = new FileReader();
        reader.readAsDataURL(theFile);
        reader.onloadend = (finishedEvent) =>{
            const {currentTarget : { result }} = finishedEvent;
            setImageList((imageList)=>[...imageList, result]);
        }
    }

    return(
            <Wrapper>
                <div>
                    <input className='button' type='file' onChange={onFileChange} multiple/> 
                </div>
                <Img> 
                {imageList.map((image) => {
                    return (
                        <img className='product' src={image} />
                    );
                })}
                    {/* <img src={imageList[0]} alt='img' width={'300px'} height={'300px'} /> */}
                </Img>
            </Wrapper>
    );
};

export default Grid;
