import React, { Fragment } from 'react';
import styled from 'styled-components';
// import {useHistory} from 'react-router-dom';

const Wrapper = styled.section`
    width: 100%;

    .button {
        margin-top: 50px;
    }
`;

const Grid = () => {
    const onFileChange = (e) => {

const {
            target : {files},
        } = e;
        const theFile = files[0];
        const reader = new FileReader();
        reader.readAsDataURL(theFile);
        reader.onloadend = (finishedEvent) =>{
            const {currentTarget : { result }} = finishedEvent;
            setImage(result);
        }
    }
    return(
            <Wrapper>
            <form onsSubmit={onSubmit} action='upload' encType='multipart/form-data'>
                <div>
                <input className='button' name='file' type='file' onChange={onFileChange} accept='image/*' multiple required/> 
                    {/* fileUpload(this) */}
                    </div>
                <input className='submit' type='submit' />
                {image && (
                    <div>
                        <img src={image} alt='img' width={'50px'} height={'50px'} />
                        <button onClick={onClearPhotoClick}>취소</button>
                    </div>
                )}
                </form>    
            </Wrapper>
    );
};

export default Grid;  


    function preview() {
    var tmp = new Image;
    tmp.src = document.form.file.value;
    document.view.src = tmp.src;
    document.view.style.display = "block";
    document.view.width = 130;
    document.view.height = 130;
    }
