import React, { Fragment } from 'react';
import styled from 'styled-components';
// import {useHistory} from 'react-router-dom';

const Wrapper = styled.section`
    width: 100%;

    .button {
        margin-top: 50px;
    }
`;

const Grid = () => {
    function reviewUploadImg(fileObj)
    {
    var filePath = fileObj.value;
    var fileName = filePath.substring(filePath.lastIndexOf("\\")+1);
    var fileKind = fileName.split(".")[1];
    }

    const elImage = document.querySelector("#reviewImageFileOpenInput");
elImage.addEventListener("change", (evt) => {
    const image = evt.target.files[0];
    if(!validImageType(image)) { 
    console.warn("invalide image file type");
    return;
    }
});

function valideImageType(image) {
    const result = ([ 'image/jpeg',
                    'image/png',
                    'image/jpg' ].indexOf(image.type) > -1);
    return result;
}
    return(
        <Fragment>
            <Wrapper>
                <form method='post' action='upload' encType='multipart/form-data'>
                <div>
                <input className='button' name='file' type='file' onChange='reviewUploading(this)' accept='image/*' multiple required/> 
                    {/* fileUpload(this) */}
                    </div>
                    <input className='submit' type='submit' />   
                </form>    
            </Wrapper>
        </Fragment>
    );
};

export default Grid;  


    function preview() {
    var tmp = new Image;
    tmp.src = document.form.file.value;
    document.view.src = tmp.src;
    document.view.style.display = "block";
    document.view.width = 130;
    document.view.height = 130;
    }
