import React ,{ useEffect, useState} from 'react';
import styled from 'styled-components';
import { atom, useRecoilState } from 'recoil';


const Wrapper = styled.section`
    width: 100%;
    max-width: 1680px;
    .button {
        margin-top: 50px;
    }
`;


const List = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);

    .product {
        width:300px;
        height: 300px;
        margin: 10px;
    }
`;

const OutsideBox = styled.div`
    border: 2px solid #dfdfdf;
`;

const InsideBox = styled.div`
    border: 1px solid #dfdfdf;
`;

const state = atom({
    key: 'grid',
    default: [],
});

const Grid = () => {

    const [imageList, setImageList] = useRecoilState(state);
    const [arrNum, setArrNum] = useState(0);

    useEffect(() => {
        setArrNum([imageList.length]);
    })

    console.log(arrNum);


    const onFileChange = (e) => {
    const { target : {files} } = e;
        const theFile = files[0];
        const reader = new FileReader();
        reader.readAsDataURL(theFile);
        reader.onloadend = (finishedEvent) =>{
            const {currentTarget : { result }} = finishedEvent;
            setImageList((imageList)=>[...imageList, result]);
        }
    }


    return(
        <Wrapper>
            <OutsideBox>
                <InsideBox>
                </InsideBox>
            </OutsideBox>
                <div>
                    <input className='button' type='file' onChange={onFileChange} multiple/> 
                </div>
                <List> 
                {imageList.map((image, idx) => {
                    return (
                        <>
                            <div>{idx+1}
                            <img className='product' src={image} /></div>
                        </>
                    );
                })}
                </List>
            </Wrapper>
    );
};

export default Grid;
